package arrays;
import java.util.*;
class struct{
	int data;
	String str;
	float fl;
}
public class structarray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	struct in=new struct();
	in.data=1;
	in.fl=(float) 0.5;
	in.str="sudarshan";
	System.out.println(in.data);
	System.out.println(in.fl);
	System.out.println(in.str);
	}
}
