package Weektest;
import java.util.*;
public class q3 {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner scan=new Scanner(System.in);
		int n=scan.nextInt();
		for(int i=0;i<n;i++) {
		int a=i;
		for(int j=a;j>=0;j--) {
			System.out.print(j+" ");
		}
		for(int k=1;k<=a;k++) {
			System.out.print(k+" ");
		}
		//for(int )
		System.out.println();
		}
		
	}
}
